
/**
 *  | This module defines the `State` monad.
 */
"use strict";
var Prelude = require("Prelude");
var Control_Monad_State_Class = require("Control.Monad.State.Class");
var Control_Monad_State_Trans = require("Control.Monad.State.Trans");
var Data_Identity = require("Data.Identity");
var Data_Tuple = require("Data.Tuple");

/**
 *  | Modify the state in a `State` action
 */
var withState = Control_Monad_State_Trans.withStateT;

/**
 *  | Run a computation in the `State` monad
 */
var runState = function (s) {
    return function ($0) {
        return Data_Identity.runIdentity(Control_Monad_State_Trans.runStateT(s)($0));
    };
};

/**
 *  | Change the type of the result in a `State` action
 */
var mapState = function (f) {
    return Control_Monad_State_Trans.mapStateT(function ($1) {
        return Data_Identity.Identity(f(Data_Identity.runIdentity($1)));
    });
};

/**
 *  | Run a computation in the `State` monad, discarding the result
 */
var execState = function (m) {
    return function (s) {
        return Data_Tuple.snd(runState(m)(s));
    };
};

/**
 *  | Run a computation in the `State` monad, discarding the final state
 */
var evalState = function (m) {
    return function (s) {
        return Data_Tuple.fst(runState(m)(s));
    };
};
module.exports = {
    withState: withState, 
    mapState: mapState, 
    execState: execState, 
    evalState: evalState, 
    runState: runState
};
