"use strict";
var Prelude = require("Prelude");
var DOM = require("DOM");
var Control_Monad_Aff = require("Control.Monad.Aff");
var Control_Monad_Eff_Class = require("Control.Monad.Eff.Class");
var Routing_Hash = require("Routing.Hash");
var setHash = function (hash) {
    return Control_Monad_Eff_Class.liftEff(Control_Monad_Aff.monadEffAff)(Routing_Hash.setHash(hash));
};
var modifyHash = function (func) {
    return Control_Monad_Eff_Class.liftEff(Control_Monad_Aff.monadEffAff)(Routing_Hash.modifyHash(func));
};
module.exports = {
    setHash: setHash, 
    modifyHash: modifyHash
};
